import React,{Component} from 'react';
import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';
import ListGroup from 'react-bootstrap/ListGroup'

class TodoList extends Component {
    constructor(){
        console.log('reset');
        super()
        this.state = {
            listOfTodos:['Eat','Sleep','Code'],
            newTask :""
        }
        this.handleChange = this.handleChange.bind(this);
        this.AddToList = this.AddToList.bind(this);
    }

    AddToList(event){
        event.preventDefault();
        this.setState({ 
            listOfTodos: [...this.state.listOfTodos,this.state.newTask],
            newTask :""
          });
    }
    DeleteFromList(value){
        this.setState(prevState => ({
            data: prevState.data.filter(el => el != value )
        }));
     }
    handleChange(event) {
        this.setState({newTask: event.target.value});
      }
    render(){
        return (
            <div className="">
                <div>Todo list to keep me bussy </div>
                <p></p>

                <Form inline onSubmit={this.AddToList}>
                    <Form.Control value={ this.state.newTask } 
                    placeholder='Todo' 
                    onChange={this.handleChange}></Form.Control>
                    <Button type="submit">Add</Button>
                </Form>

                <p></p>

                <div>
                    <ListGroup>
                    { this.state.listOfTodos.map((value,idx)=>{
                        return <ListGroup.Item 
                        style={{ fontSize:12, color: 'black' }} 
                        key={idx}>    {value} 
                       <Button type="button" class="btn btn-default" aria-label="Left Align"> X </Button>
                         </ListGroup.Item>
                    })}
                </ListGroup>
                </div>
            </div>
        )
    }
}
export default TodoList
