import React,{Component} from 'react';
import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';
import ListGroup from 'react-bootstrap/ListGroup'

class TodoList extends Component {
    constructor(){
        console.log('reset');
        super()
        this.state = {
            listOfTodos:['Eat','Sleep','Code',],
            newTask :""
        }
        this.handleChange = this.handleChange.bind(this);
        this.AddToList = this.AddToList.bind(this);
    }

    AddToList(event){
        event.preventDefault();
        this.setState({ 
            listOfTodos: [...this.state.listOfTodos,this.state.newTask],
            newTask :""
          });
    }

    handleChange(event) {
        this.setState({newTask: event.target.value});
      }
    render(){
        return (
            <div className="">
                <div>Todo list to keep me bussy </div>
                <p></p>

                <Form inline onSubmit={this.AddToList}>
                    <Form.Control value={ this.state.newTask } 
                    placeholder='Todo' 
                    onChange={this.handleChange}></Form.Control>
                    <Button type="submit">Add</Button>
                </Form>

                <p></p>

                <div>
                    <ListGroup>
                    { this.state.listOfTodos.map((value,idx)=>{
                        return <ListGroup.Item style={{ fontSize:12, color: 'black' }} key={idx}> {value} </ListGroup.Item>
                    })}
                </ListGroup>
                </div>
            </div>
        )
    }
}
export default TodoList
